"use strict";
var capital_first_letter_1 = require('./capital-first-letter');
describe('capital first letter', function () {
    it('should captial first letter', function () {
        var source = 'beijing';
        var pipe = new capital_first_letter_1.CapitalFirstPipe();
        var result = 'Beijing';
        var actual = pipe.transform(source);
        expect(actual).toEqual(result);
    });
});
